import { Component } from '@angular/core';

@Component({
  selector: 'app-login-page',
  templateUrl: './login-page.component.html',
  styleUrls: ['./login-page.component.scss']
})
export class LoginPageComponent {
  passType: string = "password";
  isText: boolean = false;
  eyeIcon: string = "fa-eye-slash"
  constructor() {

  }

  ngOnInit(){

  }
  hideShowPass(){
    this.isText = !this.isText;
    this.isText ? this.eyeIcon = "fa-eye" : this.eyeIcon = "fa-eye-slash";
    this.isText ? this.passType = "text" : this.passType = "password";
  }
}


<div class="outer">
    <div class="container">
        <div class="row">
            <div class="plate">
                <div class="panel-border">
                    <div class="panel-header" >
                        <h3 style="padding-top: 1rem; font-weight: bold;">Sign In</h3>
                    </div>
                    <div class="panel-body" style="padding: 1rem;">
                        <form>
                            <div class="input-box" >
                                <span class="fa fa-user custom-user" ></span>
                                <input type="text" placeholder="Email" required>
                            </div>
                            <div class="input-box" >
                                <span class="fa fa-lock custom-user"></span>
                                <input [type]="passType" placeholder="Password" required>
                                <span (click)="hideShowPass()" class="fa {{eyeIcon}} fa-eye-slash-btn" style="padding-right: 4px;"></span>
                            </div>
                            <button type="submit" class="btn-block">Login</button>
                            <div class="register-link" style="font: 14px -apple-system,BlinkMacSystemFont,'Segoe UI',Roboto,Helvetica,Arial,sans-serif;">
                                <p>Don't have an account? <a id="forget" routerLink="/signup">Sign Up</a></p>
                            </div>
                        </form>
                    </div>

                </div>
            </div>
        </div>
    </div>
</div>
